using Microsoft.AspNetCore.Hosting;using Microsoft.Extensions.Hosting;using System;using System.Diagnostics.CodeAnalysis;namespace IdentityServer{    /// <summary>    /// The app.    /// </summary>    /// <remarks>    /// Docs:    /// https://docs.microsoft.com/en-us/dotnet/core/versions/selection#self-contained-deployments-include-the-selected-runtime    /// </remarks>    [ExcludeFromCodeCoverage]    public class Program    {        public static int Main(string[] args)        {            CreateHostBuilder(args).Build().Run();            return 0;        }        public static IHostBuilder CreateHostBuilder(string[] args) =>            Host.CreateDefaultBuilder(args)                .ConfigureWebHostDefaults(webBuilder =>                {                    webBuilder.UseStartup<Startup>();                });    }}